import React, { useState, useContext } from 'react';
import { AppContext } from '../context/AppContext'; // CORRIGIDO: Caminho completo com /context/

const Login = () => {
  const [username, setUsername] = useState('');
  const { login } = useContext(AppContext);

  const handleSubmit = (e) => {
    e.preventDefault();
    const trimmedUsername = username.trim();
    if (trimmedUsername && trimmedUsername.length <= 50) {
      login(trimmedUsername);
    } else {
      alert('Nome inválido. Digite um nome com até 50 caracteres.');
    }
  };

  return (
    <div className="login-container">
      <h1>PlanetTech Chat</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Digite seu nome"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="login-input"
          aria-label="Nome do usuário"
          maxLength={50}
          required
        />
        <button type="submit" className="login-btn" aria-label="Entrar no chat">
          Entrar
        </button>
      </form>
    </div>
  );
};

export default Login;
